import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '../views/HomePage.vue';
import AppHotels from '../components/AppHotels.vue';
import axios from 'axios';
import AppLogin from '@/components/auth/AppLogin.vue';

const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage,
    meta: { requiresAuth: true }, // Protect this route
  },
  {
    path: '/hotels',
    name: 'AppHotels',
    component: AppHotels,
    // meta: {
    //     hideSidebar: true, // Custom property to hide the sidebar
    //   },
  },
  {
    path: '/login',
    name: 'AppLogin',
    component: AppLogin,
    meta: {
        hideSidebar: true, // Custom property to hide the sidebar
      },
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach(async (to, from, next) => {
  if (to.meta.requiresAuth) {
    try {
      const response = await axios.get('/user');
      // Check if the user is authenticated
      if (response.data && response.data.authenticated) {
        next(); // Allow navigation
      } else {
        next({ name: 'AppLogin' }); // Redirect to login
    }
} catch (error) {
    // Handle unauthenticated state or other errors
    next({ name: 'AppLogin' }); // Redirect to login
    console.log(response)
    }
  } else {
    next(); // Always allow navigation for non-protected routes
  }
});

export default router;
