import { createApp } from 'vue'; // Vue 3 uses createApp
import App from './App.vue';
import router from './router';
import axios from 'axios';

// Set Axios base URL
axios.defaults.baseURL = 'http://127.0.0.1:8000';

// Import Bootstrap CSS and JS
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

// Include CSRF token from Laravel
// If Laravel sets a CSRF token in the cookies (with 'XSRF-TOKEN'), Axios can automatically add it to requests
axios.defaults.withCredentials = true;

// Add an interceptor to automatically attach CSRF token from cookies
axios.interceptors.request.use((config) => {
  // Laravel uses X-XSRF-TOKEN for CSRF
  const token = document.cookie
    .split('; ')
    .find(row => row.startsWith('XSRF-TOKEN='))
    ?.split('=')[1];

  if (token) {
    config.headers['X-XSRF-TOKEN'] = decodeURIComponent(token);
  }

  return config;
}, (error) => {
  return Promise.reject(error);
});

const app = createApp(App);

// Add Axios globally to the app
app.config.globalProperties.$axios = axios;

// Use the router
app.use(router);

// Mount the app to the #app element
app.mount('#app');
