import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'http://127.0.0.1:8000', // Your backend API base URL
  withCredentials: true, // Include cookies or credentials if needed
});

axios.defaults.baseURL = 'http://localhost:8000'; // Laravel server URL


export default {
  // Example API call to get data
  getCsrfToken() {
    return axios.get('/get-csrf').then((response) => response.data.csrfToken);

},

  // Example API call to fetch a list
  fetchData(endpoint) {
    return apiClient.get(endpoint);
  },

  // Example API call to send data
  postData(endpoint, data) {
    return apiClient.post(endpoint, data);
  },

  // Example API call to update data
  updateData(endpoint, data) {
    return apiClient.put(endpoint, data);
  },

  // Example API call to delete data
  deleteData(endpoint) {
    return apiClient.delete(endpoint);
  },
};
